import styled from 'styled-components'

const Form = styled.form`
border: 1px solid red;

  input, textarea {
		width: 30%;
		margin-bottom: 1%;
		padding: 1em 0;
	}
	
`

const Container = styled.div`
width: 80%;
margin: 0 auto;
margin-top: 5%;
display: flex;
flex-direction: column;
`

const Header = styled.header`
  display: flex;
	flex-direction: column;
`

const Contact = () => {
  return (
   <Container>
		 <Header>
			 <h3>Get in Touch</h3>
			 <p>If you have any questions about my work or anything in general, please fill out the form below.</p>
		 </Header>
		  <Form name="contact" method="POST" data-netlify="true">
  <p>
    <label><input type="text" name="name" placeholder='Name' /></label>
  </p>
  <p>
    <label><input type="email" name="email" placeholder='Your email address' /></label>
  </p>

  <p>
    <label><textarea name="message" placeholder='Message' ></textarea></label>
  </p>
  <p>
    <button type="submit">Send</button>
  </p>
</Form>
	 </Container>
  )
}

export default Contact
